import React from 'react';
import useCountdown from './useCountdown'; // 确保路径正确

function ToDoColumn({ tasks, removeTask }) {
  return (
    <div className="column">
      <h2>待办事项</h2>
      <ul>
        {tasks.map((task) => {
          const { days, hours, minutes, seconds } = useCountdown(task.deadline);
          return (
            <li key={task.id}>
              <span>{task.text}</span>
              {task.deadline && (
                <span>
                  {days}天 {hours}小时 {minutes}分钟 {seconds}秒
                </span>
              )}
              <button onClick={() => removeTask(task.id, 'todo')}>删除</button>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default ToDoColumn;
