// ToDoColumn.js
import React from 'react';
import Countdown from './Countdown'; // 确保正确引用了 Countdown 组件


function ToDoColumn({ tasks, removeTask, moveTaskToColumn }) {
  // Column 中的其他代码不变
  // ...

  return (
    <div className="column todo">
      <h2>待办事项</h2>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <span>{task.text}</span>
            {/* ... Countdown 和 删除按钮等 ... */}
            {/* 添加移动任务的按钮 */}
            <button onClick={() => moveTaskToColumn(task.id, 'todo', 'inProgress')}>移动到进行中</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ToDoColumn;
