import React, { useState } from 'react';

function ToDoColumn({ tasks, addTask }) {
  const [newTask, setNewTask] = useState('');

  const handleAddTask = () => {
    if (newTask) {
      addTask({ id: Date.now(), text: newTask }, 'todo');
      setNewTask('');
    }

    function ToDoColumn({ tasks, addTask, removeTask }) {
        return (
          <div className="column todo">
            <h2>待办事项</h2>
            <ul>
              {tasks.map(task => (
                <li key={task.id}>
                  {task.text}
                  <button onClick={() => removeTask(task.id, 'todo')}>删除</button>
                </li>
              ))}
            </ul>
          </div>
        );
      }

  };

  return (
    <div className="column todo">
      <h2>待办事项</h2>
      <input
        type="text"
        value={newTask}
        onChange={(e) => setNewTask(e.target.value)}
        placeholder="添加新任务..."
      />
      <button onClick={handleAddTask}>添加任务</button>
      <ul>
        {tasks.map(task => (
          <li key={task.id}>{task.text}</li>
        ))}
      </ul>
    </div>
  );
}

export default ToDoColumn;
