// ToDoColumn.js
import React from 'react';
import Countdown from './Countdown'; // 确保正确引用了 Countdown 组件

function ToDoColumn({ tasks, removeTask }) {
  return (
    <div className="column todo">
      <h2>待办事项</h2>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <span>{task.text}</span>
            {/* 使用 Countdown 组件来显示截止日期的倒计时 */}
            <Countdown deadline={task.deadline} />
            <button onClick={() => removeTask(task.id, 'todo')}>删除</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ToDoColumn;
