import React from 'react';
import { Droppable, Draggable } from 'react-beautiful-dnd';

function DoneColumn({ tasks, removeTask, provided }) {
  const renderRemainingTime = (deadline) => {
    if (!deadline) return '';
    const timeLeft = new Date(deadline) - new Date();
    if (timeLeft <= 0) return '已截止';

    const daysLeft = Math.floor(timeLeft / (1000 * 60 * 60 * 24));
    return `${daysLeft}天剩余`;
  };

  return (
    <div className="column done" ref={provided.innerRef} {...provided.droppableProps}>
      <h2>已完成</h2>
      <ul>
        {tasks.map((task, index) => (
          <Draggable key={task.id} draggableId={task.id} index={index}>
            {(provided) => (
              <li ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                {task.text}
                <span> {renderRemainingTime(task.deadline)}</span>
                <button onClick={() => removeTask(task.id, 'done')}>删除</button>
              </li>
            )}
          </Draggable>
        ))}
        {provided.placeholder}
      </ul>
    </div>
  );
}

export default DoneColumn;
