import React from 'react';

function InProgressColumn({ tasks, removeTask }) {
  const renderDeadline = (deadline) => {
    if (!deadline) return '无截止日期';
    const timeLeft = new Date(deadline).getTime() - new Date().getTime();
    if (timeLeft <= 0) {
      return '截止时间已过';
    }
    const daysLeft = Math.floor(timeLeft / (1000 * 60 * 60 * 24));
    return ` ,剩余 ${daysLeft} 天`;
  };

  return (
    <div className="column in-progress">
      <h2>待办事项</h2>
      <ul>
        {tasks.map((task, index) => (
          <li key={task.id}>
            <span>{task.text}</span>
            <span>{renderDeadline(task.deadline)}</span>
            <button onClick={() => removeTask(task.id, 'inProgress')}>删除</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default InProgressColumn;
