import React from 'react';
import { Droppable, Draggable } from 'react-beautiful-dnd';

function ToDoColumn({ tasks, removeTask }) {
    const renderDeadline = (deadline) => {
      if (!deadline) return '无截止日期';
      const timeLeft = new Date(deadline).getTime() - new Date().getTime();
      if (timeLeft <= 0) {
        return '截止时间已过';
      }
      const daysLeft = Math.floor(timeLeft / (1000 * 60 * 60 * 24));
      return `剩余 ${daysLeft} 天`;
    };

  return (
    <div className="column todo" ref={provided.innerRef} {...provided.droppableProps}>
      <h2>待办事项</h2>
      <ul>
        {tasks.map((task, index) => (
          <Draggable key={task.id} draggableId={task.id} index={index}>
            {(provided) => (
              <li ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                {task.text}
                <span> {renderRemainingTime(task.deadline)}</span>
                <button onClick={() => removeTask(task.id, 'todo')}>删除</button>
              </li>
            )}
          </Draggable>
        ))}
        {provided.placeholder}
      </ul>
    </div>
  );
}

export default ToDoColumn;
