import React, { useState } from 'react';
import './App.css';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';
import TaskInput from './components/TaskInput';
import ToDoColumn from './components/ToDoColumn';
import InProgressColumn from './components/InProgressColumn';
import DoneColumn from './components/DoneColumn';

function App() {
  const [tasks, setTasks] = useState({
    todo: [],
    inProgress: [],
    done: []
  });

  const addTask = (task, column) => {
    task.id = `task-${Date.now()}`; // 创建一个更加唯一的ID
    const newTasks = { ...tasks };
    newTasks[column].push({ ...task, id: `task-${Date.now()}` });
    setTasks(newTasks);
  };

  const removeTask = (taskId, column) => {
    const newTasks = { ...tasks };
    newTasks[column] = newTasks[column].filter(task => task.id !== taskId);
    setTasks(newTasks);
  };

  const onDragEnd = result => {
    console.log('Drag ended:', result);
    const { source, destination } = result;
    if (!destination) {
      return; // dropped outside the list
    }

    if (source.droppableId === destination.droppableId && source.index === destination.index) {
      return; // dropped in the same place
    }

    const sourceCol = source.droppableId;
    const destCol = destination.droppableId;
    const sourceItems = Array.from(tasks[sourceCol]);
    const [removed] = sourceItems.splice(source.index, 1);
    const destItems = sourceCol === destCol ? sourceItems : Array.from(tasks[destCol]);
    destItems.splice(destination.index, 0, removed);

    setTasks({
      ...tasks,
      [sourceCol]: sourceItems,
      [destCol]: destItems
    });
  };

  return (
    <DragDropContext onDragEnd={onDragEnd}>
      <TaskInput addTask={addTask} />
      <Droppable droppableId="todo">
        {(provided, snapshot) => {
          console.log('Provided todo:', provided);
          return (
            <ToDoColumn tasks={tasks.todo} removeTask={removeTask} provided={provided} snapshot={snapshot} />
          );
        }}
      </Droppable>
      <Droppable droppableId="inProgress">
        {(provided, snapshot) => {
          console.log('Provided inProgress:', provided);
          return (
            <InProgressColumn tasks={tasks.inProgress} removeTask={removeTask} provided={provided} snapshot={snapshot} />
          );
        }}
      </Droppable>
      <Droppable droppableId="done">
        {(provided, snapshot) => {
          console.log('Provided done:', provided);
          return (
            <DoneColumn tasks={tasks.done} removeTask={removeTask} provided={provided} snapshot={snapshot} />
          );
        }}
      </Droppable>
    </DragDropContext>
  );
}

export default App;
