// DoneColumn.js
import React from 'react';
import Countdown from './Countdown';

function DoneColumn({ tasks, removeTask, moveTaskToColumn }) {
  const renderDeadline = (deadline) => {
    if (!deadline) return 'No Deadline';
    const timeLeft = new Date(deadline).getTime() - new Date().getTime();
    if (timeLeft <= 0) {
      return 'Passed Deadline';
    }
    const daysLeft = Math.floor(timeLeft / (1000 * 60 * 60 * 24));
    return ` , ${daysLeft} days left`;
  };

  function DoneColumn({ tasks, removeTask, moveTaskToColumn }) {
    const handleCompleteTask = (taskId) => {
      removeTask(taskId, 'done');  // 调用 removeTask 来删除任务
      alert("恭喜你！完成任务！");  // 弹出完成任务的确认窗口
    };

    return (
      <div className="column done">
        <h2>Done</h2>
        <ul>
          {tasks.map((task) => (
            <li key={task.id} className="task-item">
              <span className="task-text">{task.text}</span>
              <br></br>
              <span className="countdown">
                <Countdown deadline={task.deadline} />
              </span>
              <br></br>
              <div className="task-buttons">
                <button onClick={() => removeTask(task.id, 'done')}>Delete</button>
                <button onClick={() => moveTaskToColumn(task.id, 'done', 'todo')}>Move to To Do</button>
                <button onClick={() => moveTaskToColumn(task.id, 'done', 'inProgress')}>Move to In Progress</button>
                <button onClick={() => handleCompleteTask(task.id)}>Done</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}
export default DoneColumn;
